import React from 'react';
import { Link } from 'react-router-dom';
import { useAppContext } from '../Context/AppContext';
import Card from '../Card'; // Importa el componente Card

function Home() {
  const { state, dispatch } = useAppContext();

  const toggleFavorite = (dentist) => {
    // Verifica si el dentista ya está en favoritos
    const isFavorite = state.apiData.favorites.some((fav) => fav.id === dentist.id);

    if (isFavorite) {
      // Si es un favorito, lo eliminamos
      const updatedFavorites = state.apiData.favorites.filter((fav) => fav.id !== dentist.id);
      dispatch({ type: 'SET_API_DATA', payload: { ...state.apiData, favorites: updatedFavorites } });
      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
    } else {
      // Si no es un favorito, lo agregamos
      const updatedFavorites = [...state.apiData.favorites, dentist];
      dispatch({ type: 'SET_API_DATA', payload: { ...state.apiData, favorites: updatedFavorites } });
      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
    }
  };

  return (
    <div>
      <h1>Lista de Dentistas</h1>
      <ul>
        {state.apiData.dentists.map((dentist) => (
          <li key={dentist.id}>
            {/* Utiliza el componente Card para mostrar la información del dentista */}
            <Card
              name={dentist.name}
              username={dentist.username}
              id={dentist.id}
              toggleFavorite={() => toggleFavorite(dentist)}
              isFavorite={state.apiData.favorites.some((fav) => fav.id === dentist.id)}
            />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
