import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import Card from '../Components/Card'; // Asegúrate de que la ruta de importación sea correcta

import { AppContext, App } from '../Context/AppContext'; // Importa el contexto correctamente

function Home() {
  const { state, dispatch } = useContext(AppContext);

  const toggleFavorite = (dentist) => {
    // Verifica si el dentista ya está en favoritos
    const isFavorite = state.apiData.favorites.some((fav) => fav.id === dentist.id);

    if (isFavorite) {
      // Si es un favorito, lo eliminamos
      const updatedFavorites = state.apiData.favorites.filter((fav) => fav.id !== dentist.id);
      dispatch({ type: 'SET_API_DATA', payload: { ...state.apiData, favorites: updatedFavorites } });
      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
    } else {
      // Si no es un favorito, lo agregamos
      const updatedFavorites = [...state.apiData.favorites, dentist];
      dispatch({ type: 'SET_API_DATA', payload: { ...state.apiData, favorites: updatedFavorites } });
      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
    }
  };

  return (
    <div>
      <h1>Lista de Dentistas</h1>
      <ul>
        {state.apiData.dentists.map((dentist) => (
          <li key={dentist.id}>
            <Link to={`/dentist/${dentist.id}`}>{dentist.name}</Link>
            <button onClick={() => toggleFavorite(dentist)}>
              {state.apiData.favorites.some((fav) => fav.id === dentist.id)
                ? 'Eliminar de favoritos'
                : 'Agregar a favoritos'}
            </button>
            <Card name={dentist.name} username={dentist.username} id={dentist.id} /> {/* Renderiza la Card aquí */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
