import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AppContext, useAppContext } from "../Context/AppContext"; // Asegúrate de importar el contexto adecuado

const Card = ({ name, username, id }) => {
  const { state, dispatch } = useAppContext(AppContext);

  const toggleFavorite = () => {
    // Verifica si el dentista ya está en favoritos
    const isFavorite = state.apiData.favorites.some((fav) => fav.id === id);

    if (isFavorite) {
      // Si es un favorito, lo eliminamos
      const updatedFavorites = state.apiData.favorites.filter(
        (fav) => fav.id !== id
      );
      dispatch({
        type: "SET_API_DATA",
        payload: { ...state.apiData, favorites: updatedFavorites },
      });
      localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
    } else {
      // Si no es un favorito, lo agregamos
      const updatedFavorites = [...state.apiData.favorites, { name, username, id }];
      dispatch({
        type: "SET_API_DATA",
        payload: { ...state.apiData, favorites: updatedFavorites },
      });
      localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
    }
  };

  return (
    <div className="card">
      {/* Mostramos name, username y el id */}
      <h2>{name}</h2>
      <p>{username}</p>
      <p>ID: {id}</p>

      {/* Agregamos un Link a la página de detalle */}
      <Link to={`/dentist/${id}`}>Ver detalles</Link>

      {/* Agregamos un botón para agregar a favoritos */}
      <button onClick={toggleFavorite} className="favButton">
        {state.apiData.favorites.some((fav) => fav.id === id)
          ? "Eliminar de favoritos"
          : "Agregar a favoritos"}
      </button>
    </div>
  );
};

export default Card;
