import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { useAppContext } from '../Context/AppContext'; // Asegúrate de que esta ruta sea correcta

function Home() {
  const { state, dispatch } = useAppContext();

  // ...

  return (
    <div>
      <h1>Lista de Dentistas</h1>
      <ul>
        {state.apiData.dentists.map((dentist) => (
          <li key={dentist.id}>
            <Link to={`/dentist/${dentist.id}`}>{dentist.name}</Link>
            <button onClick={() => toggleFavorite(dentist)}>
              {state.apiData.favorites.some((fav) => fav.id === dentist.id)
                ? 'Eliminar de favoritos'
                : 'Agregar a favoritos'}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
