import React, { createContext, useReducer, useContext } from 'react';

// Define el estado inicial
const initialState = {
  theme: 'light', // Inicialmente, el tema es claro
  apiData: null, // Inicialmente, no hay datos de la API
};

// Define las acciones para el useReducer
const actionTypes = {
  TOGGLE_THEME: 'TOGGLE_THEME',
  SET_API_DATA: 'SET_API_DATA',
};

// Define el reducer para manejar el estado global
const appReducer = (state, action) => {
  switch (action.type) {
    case actionTypes.TOGGLE_THEME:
      return {
        ...state,
        theme: state.theme === 'light' ? 'dark' : 'light', // Cambiar el tema
      };
    case actionTypes.SET_API_DATA:
      return {
        ...state,
        apiData: action.payload, // Actualizar los datos de la API
      };
    default:
      return state;
  }
};

// Crea el contexto
const AppContext = createContext();

// Proveedor del contexto
export const AppProvider = ({ children }) => {
  const [state, dispatch] = useReducer(appReducer, initialState);

  return (
    <AppContext.Provider value={{ state, dispatch }}>
      {children}
    </AppContext.Provider>
  );
};

// Hook personalizado para acceder al contexto
export const useAppContext = () => {
  return useContext(AppContext);
};
